import React from 'react';
import { useLocation } from 'react-router-dom'; // Import useLocation hook

export const PaymentSuccessful = () => {
    const location = useLocation(); // Use the useLocation hook to access the current location object
    const { shoppingCart, totalCost, discount } = location.state || { shoppingCart: { products: new Map() }, totalCost: 0, discount: 0 }; // Safely access state

    // Logging the received data from navigation state for debugging
    console.log("Location State:", location.state);
    console.log("Shopping Cart:", shoppingCart);
    console.log("Total Cost:", totalCost);
    console.log("Discount:", discount);

    const products = Array.from(shoppingCart.products.values());
    const discountedTotalCost = totalCost - discount;

    return (
        <div className="container mt-5">
            <h1 className="text-center">Payment Successful</h1>
            <div className="text-center">
                <h3>Your Purchase:</h3>
                <ul>
                    {products.map((item, index) => (
                        <li key={index}>
                            {item.product.name} - Quantity: {item.count} - Price per item: ${item.product.price.toFixed(2)}
                        </li>
                    ))}
                </ul>
                <p>Discount Applied: ${discount.toFixed(2)}</p>
                <p>Total Paid: ${discountedTotalCost.toFixed(2)}</p>
            </div>
        </div>
    );
};
