// src/views/cart/ShoppingCartContext.jsx
import React, { createContext, useContext, useState } from "react";

const ShoppingCartContext = createContext();

export const ShoppingCartProvider = ({ children }) => {
    const [shoppingCart, setShoppingCart] = useState(new Map());
    const [totalCost, setTotalCost] = useState(0);
    const [discount, setDiscount] = useState(0);

    const updateTotalCost = () => {
        let cost = 0;
        shoppingCart.forEach(item => {
            cost += item.product.price * item.count;
        });
        setTotalCost(cost);
    };

    return (
        <ShoppingCartContext.Provider
            value={{ shoppingCart, setShoppingCart, totalCost, updateTotalCost, discount, setDiscount }}
        >
            {children}
        </ShoppingCartContext.Provider>
    );
};

export const useShoppingCart = () => {
    return useContext(ShoppingCartContext);
};
