// src/views/cart/PaymentSuccessful.jsx
import React from 'react';
import { useLocation } from 'react-router-dom';
import { useShoppingCart } from './ShoppingCartContext';

export const PaymentSuccessful = () => {
    const location = useLocation();
    const { shoppingCart, totalCost, discount } = location.state || {};
    const products = Array.from(shoppingCart.values());
    const discountedTotalCost = totalCost - discount;

    return (
        <div className="container mt-5">
            <h1 className="text-center">Payment Successful</h1>
            <div className="text-center">
                <h3>Your Purchase:</h3>
                <ul>
                    {products.map((item, index) => (
                        <li key={index}>
                            {item.product.name} - Quantity: {item.count} - Price per item: ${item.product.price.toFixed(2)}
                        </li>
                    ))}
                </ul>
                <p>Discount Applied: ${discount.toFixed(2)}</p>
                <p>Total Paid: ${discountedTotalCost.toFixed(2)}</p>
            </div>
        </div>
    );
};
