import { Link } from "react-router-dom";
import { useParams } from "react-router";

export default function ProductList({ allproducts }) {
    const { cat } = useParams(); // Get category from URL params
    const filteredProducts = cat ? allproducts.filter(product => product.category.toLowerCase() === cat.toLowerCase()) : allproducts;
